SELECT DISTINCT "Country", COUNT("Name") AS "NumberOfAirlines"
FROM airline.airlinetable
WHERE "Country" IS NOT NULL
AND "Country" NOT IN ('Boonville Stage Line', 'S.A.', 'ALAMO', '\N', 'ACOM', 'ACTIVE AERO', 'AEROCENTER', 'AEROCESAR', 'AEROPERLAS', 'AEROPUMA', 'AEROSOL', 'AEROSUN', 'AEROVARADERO', 'AEROWEE', 'AIR-MAUR', 'AIR CLASS', 'AIR FREIGHTER', 'AIR PRINT', 'Air S', 'AIREX', 'AIRFLIGHT', 'AIRMAN', 'AIRNAT', 'AIRPAC', 'AIRPORT HELICOPTER', 'AIRWAVE', 'ALASKA', 'ALASKA PACIFIC', 'ALCON', 'ALDAWLYH AIR', 'ALL STAR', 'ALNACIONAL', 'ANTARES', 'APPALACHIAN', 'AQUILINE', 'ARIZAIR', 'ARMSTRONG', 'ASA PESADA', 'ASTORIA', 'ASUR', 'ATCO', 'ATLANTIC NICARAGUA', 'ATLANTIS CANADA', 'AUDI AIR', 'AURORA AIR', 'AUSA', 'AVEMEX', 'AVIANCA', 'AVINOR', 'AVIOQUINTANA', 'AZIMUT')
AND "Active" = 'Y'
GROUP BY "Country"
ORDER BY "NumberOfAirlines" DESC
